sys_variable_value.value:
// Generate the first payload to use
// Subsequent payloads are generated in the pagination setup step
// We do this because pagination setup only runs AFTER the first REST execution
// Not sure what the hard limit is for this call... but one probably does exist!
(function execute(inputs, outputs) {
  outputs.limit = inputs.limit;

  var firstPayloadData = inputs.updatesData.slice(0, inputs.limit);
  outputs.first_payload = JSON.stringify({ updates: firstPayloadData });
})(inputs, outputs);

