sys_variable_value.value:
(function paginate(variables, pageResponse) {
    var updatesData = fd_data.action_inputs.updates_data;
    var limit = parseInt(variables.limit);
    var offset = parseInt(variables.offset);

    var nextChunk = updatesData.slice(offset, offset + limit);
    var hasNext = Boolean(nextChunk)

    variables.offset = offset + limit;
    variables.getNextPage = hasNext;
    variables.nextPayload = JSON.stringify({ updates: nextChunk });
})(variables, pageResponse);

