sys_variable_value.value:
(function execute(inputs, outputs) {
  // Pagination setup only runs after the first REST step
  // This means we have to fall back to the setup script to generate the very first payload
  outputs.payload = inputs.paginationPayload || inputs.initialPayload;
})(inputs, outputs);

