sys_variable_value.value:
// We're forced to use a script instead of a Data Stream action for this...
// That's because the JSON splitter step refuses to process nested arrays
(function execute(inputs, outputs) {
  var message = createJiraRestMessage(inputs.aliasId, 'issue/createmeta');
  message.setQueryParameter('projectIds', inputs.projectJiraId);

  var response = message.execute();

  // Panic if the REST request itself fails
  if (response.haveError()){
    NiceError.raise(
      'Unexpected REST error:\n' +
      	response.getErrorCode() + '\n' +
      	response.getErrorMessage()
    );
  }

  outputs.response_json = response.getBody();

})(inputs, outputs);

