sys_variable_value.value:
(function execute(inputs, outputs) {
  var responseData = JSON.parse(inputs.responseJson);

  // Panic if the response data is not formatted as expected
  if (
    !responseData ||
    !responseData.projects ||
    responseData.projects.length !== 1 ||
    !responseData.projects[0].issuetypes ||
    responseData.projects[0].issuetypes.length === 0
  ){
    NiceError.raise(
      'REST response was valid JSON, but not in the expected format:\n' +
      responseRaw
    );
  }

  // Set output to the part of the response that we care about
  outputs.issue_types = responseData.projects[0].issuetypes;
})(inputs, outputs);

