sys_variable_value.value:
(function execute(inputs, outputs) {
    var payload = JSON.parse(inputs.payload);
  if(payload.projects.length!=0)
  {
    var issueTypes = payload.projects[0].issuetypes.map(function(issueType) {
        return {
            label: issueType.name,
            name: issueType.name
        };
    });
     outputs.issue_types = {
        data: issueTypes
    };
    outputs.status= "Success";
  }
  else
  {
    outputs.status ="Error";
    outputs.error_message = "Invalid Project ID.";
  }

})(inputs, outputs);

