sys_variable_value.value:
(function execute(inputs, outputs) {

  // Stolen from the Jira Spoke JiraUtils Script Include
  // For some reason, they made it inaccessible from other scopes
  // They also marked it Read-Only, so we can't manually change the access...
  function getAPIVersion(sys_alias) {
    var gr = new GlideRecord('connection_attributes');
    gr.initialize();
    gr.addQuery('model', sys_alias);
    gr.query();
    while (gr.next()) {
      if (gr.column_label.toString().toLowerCase() == 'api version') // so that capitalization will not be a problem
        return gr.default_value;
    }
    return 2;
  }

  if (inputs.project.trim() == "")
    throw new Error("Project cannot be empty");

  outputs.connection_alias_sys_id = inputs.sys_alias.sys_id;
  outputs.api_version = getAPIVersion(outputs.connection_alias_sys_id);
})(inputs, outputs);

