sys_variable_value.value:
(function execute(inputs, outputs) {
  // Helper function stolen from the JiraUtils script include
  // It's access limited for some reason?
  // Even the file itself is Read-Only...
  function getAPIVersion(sys_alias) {
    var gr = new GlideRecord('connection_attributes');
    gr.initialize();
    gr.addQuery('model', sys_alias);
    gr.query();
    while (gr.next()) {
      if (gr.column_label.toString().toLowerCase() == 'api version') // so that capitalization will not be a problem
        return gr.default_value;
    }
    return 2;
  }

  outputs.api_version = getAPIVersion(outputs.aliasId);
  // We have to manually toString this, because SN inserts commas for integers when used in Flow designer...
  // The Jira REST API doesn't like the commas
  outputs.formatted_jira_project_id = inputs.jiraProjectGr.u_jira_id.toString();
})(inputs, outputs);

