sys_variable_value.value:
(function execute(inputs, outputs) {
  var existingTypesQuery = new GlideQuery('sys_choice')
	.where('name', 'u_jira_issue')
  	.where('element', 'u_jira_type')
  	.where('dependent_value', inputs.projectId);

  var idsToDelete = existingTypesQuery
  	.select('value')
  	.filter(function(existingType){
    	return !inputs.jiraIssueTypes.some(function(jiraType){
      		return jiraType.id == existingType.value;
   		});
  	})
  	.map(function(data){ return data.sys_id; })
  	.toArray(99);

  // Delete the existing types that are no longer needed
  idsToDelete.forEach(function(id){
    new GlideQuery('sys_choice').where('sys_id', id).del();
  });

})(inputs, outputs);

