sys_variable_value.value:
(function execute(inputs, outputs) {
  var choiceQuery = new GlideQuery('sys_choice');

  var existingTypesQuery = choiceQuery
  	.where('name', 'u_jira_issue')
  	.where('element', 'u_jira_type')
  	.where('dependent_value', inputs.projectId);

  var issueData = inputs.jiraIssueTypes
  	.map(function(jiraIssueType){
    	// Get the choice record data if one already has a matching value
		var maybeExistingType = existingTypesQuery.getBy({'value': jiraIssueType.id.toString() }, ['value']);
        return GQ.merge(
          // If there is no matching choice record, default to this template
          maybeExistingType.orElse({
            name: 'u_jira_issue',
            element: 'u_jira_type',
            dependent_value: inputs.projectId,
            value: jiraIssueType.id.toString()
          }),
          // Regardless of the action taken (insert or update), always update these values
          {
            label: jiraIssueType.name,
            hint: jiraIssueType.description
          }
        );
  	});

  // The issue data may contain existing OR new Jira issue types
  // The GlideQuery.insertOrUpdate method knows how to handle the difference
  var results = issueData.map(function(data){
    return choiceQuery.insertOrUpdate(data);
  });

  outputs.newChoices = results
    .filter(function(maybeResult){ return maybeResult.isPresent(); })
  	.map(function(maybeResult){ return maybeResult.get().sys_id; });
})(inputs, outputs);

