sys_variable_value.value:
(function execute(inputs, outputs) {
  var wrappedNote = inputs.isHtml ?  '[code]' + inputs.note + '[/code]' : inputs.note;
  inputs.issueGr.work_notes.setJournalEntry(wrappedNote, inputs.userName);
  inputs.issueGr.update();
})(inputs, outputs);

