sys_variable_value.value:
(function parse(inputs, outputs) {
    var stakeholderField = inputs.fd_data._2__script_step.stakeholder_field_name;
    var data = JSON.parse(inputs.sourceItem);
    outputs.targetObject = data;

    var stakeholderData = data.fields[stakeholderField];
    outputs.targetObject.fields.stakeholder = stakeholderData && stakeholderData.value;
})(inputs, outputs)

