sys_variable_value.value:
// Generate the first payload to use
// Subsequent payloads are generated in the pagination setup step
// We do this because pagination setup only runs AFTER the first REST execution
// We HAVE to paginate, because this endpoint only accepts up to 1000 options at a time
(function execute(inputs, outputs) {
  outputs.limit = inputs.limit;
  outputs.options_string = inputs.optionValues.join();

  var firstPayloadOptions = inputs.optionValues.slice(0, inputs.limit);
  outputs.first_payload = JSON.stringify({
    options: firstPayloadOptions.map(function(optionValue){ return {value: optionValue}; })
  });
})(inputs, outputs);

