sys_variable_value.value:
(function paginate(variables, pageResponse) {
    var optionValues = variables.optionValues.split(',');

    var limit = parseInt(variables.limit);
    var offset = parseInt(variables.offset);

    var nextValues = optionValues.slice(offset, offset + limit);
    var hasNext = Boolean(nextValues)

    variables.offset = offset + limit;
    variables.getNextPage = hasNext;

    variables.nextPayload = JSON.stringify({
        options: nextValues.map(function(nextValue){ return {value: nextValue}; })
    });
    
})(variables, pageResponse);

