sys_variable_value.value:
(function paginate(variables, pageResponse) {
    var offset = parseInt(variables.startAt);
    var limit = parseInt(variables.maxResults);

    var nextStart = offset + limit;

    variables.startAt = nextStart.toString();
    variables.getNextPage = variables.isLast === 'false';
})(variables, pageResponse);

