sys_variable_value.value:
(function execute(inputs, outputs) {
  var updateObj = inputs.fields.reduce(function(obj, next){
    if (next.fields_field_type === 'choice'){
      // Choice fields expect this particular format
      obj[next.id] = {value: next.value};
    } else {
      obj[next.id] = next.value;
    }
    return obj;
  }, {});

  outputs.payload = JSON.stringify({fields: updateObj});
})(inputs, outputs);

