sys_variable_value.value:
(function parse(inputs, outputs) {

    var data = JSON.parse(inputs.sourceItem);

    function dataCopier(source, target){
        for (var key in source){
            var keyData = source[key];
            if (keyData === undefined || keyData === null) continue;
            if (typeof keyData === 'object'){
                dataCopier(source[key], target[key]);
            } else {
                target[key] = source[key];
            }
        }
    }

    //dataCopier(outputs.targetObject, data);
    outputs.targetObject = data;

})(inputs, outputs)

(function paginate(variables, pageResponse) {
    var startAt = parseInt(variables.startAt);
    var maxResults = parseInt(variables.maxResults);
    var total = parseInt(variables.total || maxResults);

    var nextStart = startAt + maxResults;
    variables.startAt = (startAt + maxResults).toString();
    variables.getNextPage = nextStart < total;
})(variables, pageResponse);

